import React, { Component } from 'react';
import Vimeo from '@u-wave/react-vimeo';
import ReactPlayer from "react-player"
import { Player, ControlBar, BigPlayButton } from 'video-react';
import extrait_adidas from ".././Preview/extrait_adidas.mp4"
import './video.css';
import "../../node_modules/video-react/dist/video-react.css";


class Videos extends Component {
    constructor() {
        super();
        this.state = {
            videos: [
                {
                    id: "prev1",
                    src: extrait_adidas
                },
                {
                    id: "vid2",
                    src: extrait_adidas
                },
                {
                    id: "prev1",
                    src: extrait_adidas
                },
                {
                    id: "vid2",
                    src: extrait_adidas
                },
            ]
        };
    }

    PlayVideo(id) {
        var video = document.getElementById(id).children[0]
        console.log(video)
        video.setAttribute('playing', 'true')
    }


    render() {
        return (
            <div className="videobox">
                {this.state.videos.map((data, i) => {
                    return (
                        <div className="video" key={i} >
                            <Player
                                fluid
                                src={data.src}
                                onMouseEnter={(e) => console.log(data.id)}
                                >
                                <ControlBar autoHide={false} className="controlbar"/>
                                <BigPlayButton position="center" className="playbutton" />
                            </Player>
                        </div>
                    )
                })}
            </div>
        );
    }
}

export default Videos
