import React, { useRef } from 'react';
import emailjs from 'emailjs-com';
import Header from './Components/Header';
import Footer from './Components/Footer';

import "./Contact.css"

export const Contact = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_d1j7u9w', 'template_poicbk5', form.current, 'user_gvqTgb9mSpJQNV6R0KmMP')
      .then((result) => {
        console.log(result.text);
      }, (error) => {
        console.log(error.text);
      });
  };

  return (
    <div>
      <Header />
      <form ref={form} onSubmit={sendEmail}class="form">
        <div className="Formbox">
          <h2>CONTACTEZ-NOUS</h2>
            <p>Votre nom</p>
            <input className="input" type="text" name="from_name" />
            <p>Votre Email</p>
            <input className="input" type="email" name="from_email" />
            <p>Message</p>
            <textarea name="message" />
          <input class="button" type="submit" value="Send" />
        </div>
      </form>
      <Footer />
    </div>
  );
};

export default Contact
