import Header from './Components/Header';
import Videos from './Components/Videos';

import './Home.css';


class Home extends Component {
    constructor() {
        super();
        this.state = {
        };
    }

Scroll() {

const checkpoint = 300;
    window.addEventListener("scroll", () => {
        const currentScroll = window.pageYOffset;
        if (currentScroll <= checkpoint) {
          opacity = 1 - currentScroll / checkpoint;
        } else {
          opacity = 0;
        }
        document.getElementById("#hometext").style.opacity = opacity;
      });
}

    render() {
        this.Scroll();
        return (
            <div>
                <div id="blackscreen">
                    <div id="hometext">
                    <p>Pierre Edouard Joubert</p>
                    <p>Realisateur</p>
                    </div>
                </div>
                <Header />
                <p>Home Page</p>
                <div id="contentbox">
                    <Videos />
                </div>

            </div>
        );
    }
}

export default Home
